import { HTMLAttributes, MouseEvent } from "react";
import { buttonSizes, buttonSkins } from "./props";
import { PhosphorIcons } from "../../Theme/Icon";
export interface IButton extends HTMLAttributes<HTMLButtonElement> {
    /** Define a text for the tag */
    text: string;
    /** Control the size of a button with `size` prop */
    size?: keyof typeof buttonSizes;
    /** Control the appearance of a button with a `skin` prop. */
    skin?: keyof typeof buttonSkins;
    /** Phosphor name icon */
    icon?: keyof typeof PhosphorIcons | "None";
    /** If `hasMinWidth` is set to false then it has no defined width */
    hasMinWidth?: boolean;
    /** Remove all background color */
    outlined?: boolean;
    /** Whether the button is disabled */
    disabled?: boolean;
    /** Enable to handle call function click */
    onClick?: (e: MouseEvent<HTMLButtonElement>) => void;
}
