import { ButtonHTMLAttributes, MouseEvent } from "react";
import { PhosphorIcons } from "../../Theme/Icon";
export type ButtonUiType = ("primary" | "secondary" | "outlined" | "text");
export type ButtonSizeType = ("small" | "medium" | "large");
export interface IChiperButton extends ButtonHTMLAttributes<HTMLButtonElement> {
    text: string;
    size?: ButtonSizeType;
    uiType?: ButtonUiType;
    icon?: keyof typeof PhosphorIcons;
    disabled?: boolean;
    onClick?: (e: MouseEvent<HTMLButtonElement>) => void;
    isLoading?: boolean;
}
