import { HTMLAttributes, MouseEvent } from "react";
import { fabSizes } from "./props";
import { PhosphorIcons } from "../../Theme/Icon";
export interface IFab extends HTMLAttributes<HTMLButtonElement> {
    /** Phosphor name icon */
    icon: keyof typeof PhosphorIcons | "None";
    /** Control the size of a button with `size` prop */
    size?: keyof typeof fabSizes;
    /** Remove all background color */
    outlined?: boolean;
    /** Whether the button is disabled */
    disabled?: boolean;
    /** Enable to handle call function click */
    onClick?: (e: MouseEvent<HTMLButtonElement>) => void;
}
