export interface IBrandCard {
    /** Unique identifier of the brand */
    id: number;
    /** A path to the brand's image */
    image: string;
    /** A name of the brand */
    brandName: string;
    /** A function without parameters that is called when the user clicks on the brand */
    onClickBrand: () => void;
    /**
     * An array of objects that represent the brand's products.
     * Each object has specific properties such as `id`, `productName`, `price`, `image`, etc
     */
    products: IProduct[];
    /** Indicates whether the component is currently in a loading state */
    isLoading: boolean;
    /** Indicates whether the brand is an advertisement or not */
    isAd?: boolean;
    /** A title of the brand's advertisement */
    adTitle?: string;
    /** The position of the brand in the array of brands to be displayed */
    indexPosition?: number;
}
export interface IProduct {
    id: number;
    name: string;
    image: string;
    discount?: string | null;
    discountPrice?: string | null;
    originalPrice: string;
    currentSymbol: string;
    onClick: (index: number) => void;
    onClickDetail: () => void;
    measurement: string;
    measurementUnit: string;
}
export interface IBrandProductDetail {
    id: number;
    name: string;
    image: string;
    discount?: string | undefined | null;
    discountPrice?: string | undefined | null;
    originalPrice: string;
    currentSymbol: string;
    onClickDetail: () => void;
    packaging: string;
}
