export interface IProductRow {
    /** define the quantity of a product */
    quantity: number;
    /** define the old quantity of a product before change */
    oldQuantity?: number;
    /** define the name and detail of a product */
    detail: string;
    /** define the currency of a product */
    currency: string;
    /** define the price of a product */
    price: number | string;
    /** informs if the product is not available */
    disabled?: boolean;
    /** informs if the product has changed quantity */
    hasChanged?: boolean;
}
