import { HTMLAttributes } from "react";
import { PhosphorIcons } from "../../Theme/Icon";
export interface IInputChip extends HTMLAttributes<HTMLSpanElement> {
    /** Define a text for the tag */
    text: string;
    /** Phosphor name icon */
    iconLeft?: keyof typeof PhosphorIcons | "None";
    /** Phosphor name icon */
    iconRight?: keyof typeof PhosphorIcons | "None";
    /** Remove all background color */
    outlined?: boolean;
    /** Whether the chip is disabled */
    disabled?: boolean;
    /** Remove the chip whit icon right  */
    removeInput?: (e: any) => void;
    /** Chip with action  */
    action?: (e: any) => void;
}
