import { ReactNode } from "react";
import { modalHeight, modalPositionX, modalPositionY, modalWidth } from "./props";
export interface IModal {
    /** Provide the contents of your Modal */
    children: JSX.Element | JSX.Element[] | ReactNode;
    /** Handler Modal status showing/hidden */
    setShow: (value: boolean) => void;
    /** Specify whether the Modal is currently open */
    show: boolean;
    /** Define a `Modal` configuration */
    config?: ModalConfig;
    /** Specify the content of the modal header title */
    title?: string;
}
export interface ModalConfig {
    /** Define Modal position on X */
    positionX?: keyof typeof modalPositionX;
    /** Define Modal position on Y */
    positionY?: keyof typeof modalPositionY;
    /** Specify the width variant */
    width?: keyof typeof modalWidth;
    /** Specify the height variant */
    height?: keyof typeof modalHeight;
    /** Define if overlay modal has to be show */
    showOverlay?: boolean;
}
