import { ReactNode } from "react";
export type ToastType = ("neutral" | "negative" | "positive" | "informative" | "attentive");
export interface IToast {
    type?: ToastType;
    title?: string;
    body?: string;
    actionText?: string;
    hideCloseIcon?: boolean;
    onCloseClick?: () => void;
    onActionTextClick?: () => void;
    className?: string;
    children?: ReactNode;
}
