import { ReactNode } from "react";
export type TooltipPosition = "up" | "down" | "left" | "right";
export interface ITooltip {
    /** Trigger element for the tooltip */
    children: ReactNode;
    /** Tooltip’s description's elements to be displayed */
    description: string | string[] | ReactNode | ReactNode[];
    /** Select tooltip’s position in relation to its trigger element */
    position?: TooltipPosition;
    /** Show close button at right-top corner */
    close?: boolean;
    /** Tooltip’s title to be displayed */
    title?: string;
    /** Show the `Tag` component on right of the `title` */
    tag?: string;
    /** Style css classes of title */
    titleClass?: string;
    /** Style css classes of tag */
    tagClass?: string;
    /** mode dark an ligth */
    modeDark?: boolean;
}
