import { ChangeEvent, HTMLAttributes, ReactNode } from "react";
export interface ICheckbox extends HTMLAttributes<HTMLInputElement> {
    /** Used to distinguish between different checkboxes */
    name?: string;
    /**
     * The type of the checkbox input.
     * @deprecated Use `RadioButtons` instead when type is "radio".
     */
    type?: "checkbox" | "radio";
    /** A boolean indicating whether the checkbox is checked or not */
    checked?: boolean;
    /** The label text for the checkbox */
    label?: string | ReactNode;
    /** When the input is checked, the value will be set to true */
    value?: string;
    /** A function that will be called when the checkbox is changed */
    onChange?: (e: ChangeEvent<HTMLInputElement>) => void;
    /** Whether the button is disabled */
    disabled?: boolean;
    "data-testid"?: string;
}
