import { HTMLAttributes, ReactNode } from "react";
export type FiltersSizes = ("small" | "medium" | "large");
export interface IFilters extends HTMLAttributes<HTMLDivElement> {
    /** Control the size of the Filters */
    size?: FiltersSizes;
    /** Indicates if the Filters is actived */
    actived?: boolean;
    /** The label text for the Filters */
    label?: string | ReactNode;
    /** Whether the button is disabled */
    disabled?: boolean;
}
