import { HTMLAttributes, MouseEvent } from "react";
import { sizes } from "./props";
export interface IFrequency extends HTMLAttributes<HTMLButtonElement> {
    /** Define a text for the tag */
    text: string;
    /** Control the size of a button with `size` prop */
    size?: keyof typeof sizes;
    /** Remove all background color */
    selected?: boolean;
    /** Whether the button is disabled */
    disabled?: boolean;
    /** Enable to handle call function click */
    onClick?: (e: MouseEvent<HTMLButtonElement>) => void;
}
