import { ChangeEvent, HTMLAttributes, ReactNode } from "react";
export interface IRadioButtons extends HTMLAttributes<HTMLInputElement> {
    /** Used to distinguish between different radioButtons */
    name?: string;
    /** A boolean indicating whether the radioButton is checked or not */
    checked?: boolean;
    /** The label text for the radioButton */
    label?: string | ReactNode;
    /** When the input is checked, the value will be set to true */
    value?: string;
    /** A function that will be called when the radioButton is changed */
    onChange?: (e: ChangeEvent<HTMLInputElement>) => void;
    /** Whether the button is disabled */
    disabled?: boolean;
    /** Identifier of the component and also allows the Label to associate */
    id?: string;
}
