import { ChangeEvent, InputHTMLAttributes } from "react";
export interface IToggle extends InputHTMLAttributes<HTMLInputElement> {
    /** Specifies whether the toggle is checked or unchecked */
    checked?: boolean;
    /** Disables the toggle if set to `true` */
    disabled?: boolean;
    /** Adds an icon to the toggle if set to `true` */
    withIcon?: boolean;
    /** A callback called when the user changes the value of the toggle */
    onChange?(e: ChangeEvent<HTMLInputElement>): void;
}
