import { ITableScroll } from "./interface";
/**
 * Use `TableScroll` as an HTML table and send required props.
 * Put the scroll bar always on the bottom.
 *
 * Used with [InfinityScroll](https://www.npmjs.com/package/react-infinite-scroll-component)
 *
 *
 * #### Usage:
 *
 * ```tsx
  import { TableScroll } from "@chiper-inc/sb-web-chiper";

  const dataColumns = [
    { title: "Dia",  key: "day" },
    { title: "Estimado",  key: "estimated" },
    { title: "Por asignar", key: "assign" },
  ];

  const dataRow = [
    {
      day: "Lunes",
      estimated: 20,
      assign: "Completo",
    },
    {
      day: "Martes",
      estimated: 16,
      assign: "Completo",
    },
  ];

  const [hasMore, setHasMore] = useState(true);
  const [items, setItems] = useState(dataRow);
  const data = useMemo(() => items, [items]);

  const fetchMore = () => {
    if (data.length >= 1) {
      setHasMore(false);
      return;
    }
    setTimeout(() => {
      setItems(items.concat(dataRow[0]));
    }, 500);
  };

  return (
    <TableScroll
      hasMore={hasMore}
      fetchMore={fetchMore}
      columns={dataColumns}
      rows={data}
    />
  );
 * ```
 *
 * #### Demo:
 */
export declare function TableScroll({ columns, rows, loader, scrollableTarget, endMessage, hasMore, fetchMore, newStyle, heightTable, }: ITableScroll): import("react/jsx-runtime").JSX.Element;
