export interface ITimePicker {
    /** Set initial value. E.g: `12-40-m.` `01-40-p.m.` `02-35-a.m.`  */
    value?: string;
    /** When the selected time changes it receives a formatted hour */
    onChange?: (formattedHour: string) => void;
    /** Representing the interval between minutes in the time picker */
    intervalMinutes?: 1 | 5 | 10 | 20 | 30;
    /** The maximum value for the minutes in the time picker */
    maximunMinutes?: number;
    /** CSS class for the main container of the time picker */
    classNameContainerMain?: string;
    /** CSS class for the height of the time picker container */
    heightClass?: string;
    /** CSS class for the width of the time picker container */
    widthClass?: string;
    /** Indicating whether the time picker is disabled or not */
    disabled?: boolean;
}
