import { Dispatch, SetStateAction } from "react";
import { IInputText } from "../../TextFields/InputText/interface";
export interface IWeekPicker extends Omit<IInputText, "value"> {
    /** Send a value with a valid Date */
    value: Date | string;
    /** Send a formatted date (expression or string) */
    format?: string;
    /** If you want to show calendar box set `true` */
    showPicker?: boolean;
    /** The minimum day, which indicates the start of the input */
    min?: Date | string;
    /** The maximum day, which indicates the limit of the input */
    max?: Date | string;
    /** Updates the input value */
    setValue: Dispatch<SetStateAction<Date | string>>;
    type?: never;
    contentAfter?: never;
    contentBefore?: never;
}
