import { PhosphorIcons } from "../../Theme/Icon";
export type IDropdownWidthType = "small" | "normal" | "full";
export type SimpleDropdownType = "simple" | "radio";
export interface IDropdownUi {
    width?: IDropdownWidthType;
    maxHeight?: string;
}
export interface IDropdownItem {
    value: string;
    text: string;
    icon?: keyof typeof PhosphorIcons;
    subItems?: IDropdownItem[];
}
export interface IDropdown extends IDropdownUi {
    items: IDropdownItem[];
}
export interface ISimpleDropdown extends IDropdown {
    selectedValue?: string;
    onItemClick: (itemValue: string) => void;
    type?: SimpleDropdownType;
}
export interface IControlledDropdown extends IDropdown {
    selectedValues?: string[];
    onConfimationClick: (itemValues: string[]) => void;
    onCancelClick?: () => void;
    leftButtonText: string;
    rightButtonText: string;
}
export interface IMultipleDropdown extends IDropdown {
    selectedValues?: string[];
    onItemClick: (itemValue: string) => void;
}
