import { Dispatch, HTMLAttributes, ReactNode, SetStateAction } from "react";
import { PhosphorIcons } from "../../Theme/Icon";
export type FiltersSizes = ("small" | "medium" | "large");
export interface IiconSelect extends HTMLAttributes<HTMLDivElement> {
    iconName: keyof typeof PhosphorIcons;
    items: {
        label: string;
        value: string;
        color?: string;
    }[];
    setValue: Dispatch<SetStateAction<string>>;
    /** Control the size of the Filters */
    size?: FiltersSizes;
    /** Indicates if the Filters is actived */
    actived?: boolean;
    /** The label text for the Filters */
    label?: string | ReactNode;
    /** Whether the button is disabled */
    disabled?: boolean;
}
