import { ChangeEvent, HTMLAttributes, ReactNode } from "react";
type InputTypeAttr = "date" | "datetime-local" | "email" | "month" | "number" | "password" | "search" | "tel" | "text" | "url" | "week";
export interface IInputText extends HTMLAttributes<HTMLInputElement> {
    /** The label associated with the field */
    label: string;
    /**
     * Current value of the input. Provide this if the input is a controlled
     * component where you are maintaining its current state; otherwise, use `defaultValue`
     */
    value: string;
    /** Called when the user changes the input's value */
    onChange(e: ChangeEvent<HTMLInputElement>): void;
    /** Define an input type */
    type?: InputTypeAttr;
    /** Whether the input is disabled */
    disabled?: boolean;
    /** Send a boolean thats verify if is a valid date */
    isValid?: boolean;
    /** Content full width */
    full?: boolean;
    /** Element before the input text */
    contentBefore?: ReactNode;
    /** Element after the input text */
    contentAfter?: ReactNode;
    /** Indicates when a field is required */
    required?: boolean;
    /** Indicates when a field is read only */
    readOnly?: boolean;
}
export {};
