import { HTMLAttributes } from "react";
export interface SearchResult {
    id: number;
    title: string;
    image?: string;
    sku?: string;
    description?: string;
}
export interface ISearchField extends Omit<HTMLAttributes<HTMLDivElement>, "onChange"> {
    /** Receives the current search query and result item */
    onChange: (query: string) => void;
    /** Get result object on click */
    onResultClick?: (result: SearchResult) => void;
    /** Clear result list */
    onClearResult?: () => void;
    /** Limit height to create scroll inside result list */
    maxHeight?: number;
    /** Wait for results list */
    hasDebounce?: boolean;
    /** This value should be updated in response to the user's input */
    value: string;
    /** Results that should be displayed in a dropdown or list below the search input */
    items: SearchResult[];
    /**
     * A flag that determines whether the search results should be
     * displayed as a list `vertical layout` or as a dropdown `horizontal layout` */
    isList?: boolean;
    /** If set to true, should not be able to interact with the search input */
    disabled?: boolean;
    /** A text that should be displayed in the search input when no value is entered yet */
    placeholder?: string;
    /** Determines whether the search input should have a `smaller` height or not */
    smallHeight?: boolean;
}
