import { ChangeEvent, HTMLAttributes } from "react";
export interface ITextArea extends HTMLAttributes<HTMLTextAreaElement> {
    /** The placeholder associated with the field */
    placeholder?: string;
    /** Input name is required */
    name?: string;
    /** Current value input */
    value?: string;
    /** Normally true if the user needs to do something before */
    disabled?: boolean;
    /** Should set true when the user types something not allowed */
    isValid?: boolean;
    /** A texd to help the user to complete the field */
    helperText?: string;
    /** Max characters allowed as the value */
    maxCharacters?: number;
    /** The height of text area */
    height?: string;
    /** Called when the user changes the input's value */
    onChange?: (e: ChangeEvent<HTMLTextAreaElement>) => void;
    "data-testid"?: string;
}
