import { ChangeEvent, InputHTMLAttributes, MouseEvent } from "react";
import { PhosphorIcons } from "../../Theme/Icon";
export interface ITextField extends InputHTMLAttributes<HTMLInputElement> {
    /** The placeholder associated with the field */
    placeholder: string;
    /** Used to distinguish between different inputs */
    name: string;
    /** Current value of the input */
    value: string;
    /** Called when the user changes the input's value */
    onChange(e: ChangeEvent<HTMLInputElement>): void;
    /** Text to provide additional information below the field */
    helperText?: string;
    /** Whether the input is disabled */
    disabled?: boolean;
    /** Send a boolean thats verify if is a valid date */
    isValid?: boolean;
    /** Content full width */
    full?: boolean;
    /** Phosphor name icon */
    icon?: keyof typeof PhosphorIcons | "None";
    rightIcon?: keyof typeof PhosphorIcons;
    onRightIconClick?: (e: MouseEvent<HTMLElement>) => void;
    "data-testid"?: string;
}
