import { ElementType, HTMLAttributes, ReactNode } from "react";
import { IColors } from "../Colors/interface";
import { textVariants } from "./props";
export interface ITypography {
    /**
     * The content to be displayed inside the Title component
     */
    children: ReactNode;
    /**
     * The `variant` of the text specified textVariants
     */
    variant?: keyof typeof textVariants;
    /**
     * The HTML markup to be used for rendering the Title component.
     * It can be rendered as any HTML markup
     */
    as?: ElementType<HTMLAttributes<HTMLHeadingElement>> | null;
    /**
     * The color of the text, selected from the predefined color palette
     */
    color?: IColors;
    /**
     * A boolean value indicating whether the text should be bold
     */
    bold?: boolean;
    "data-testid"?: string;
}
